mapper.select:
SELECT * FROM (
	SELECT ROWNUM rnum,no,title, content, writer,writeDate,hit
	FROM (
		SELECT no,title, content, writer,writeDate,hit
		FROM board

		ORDER BY no DESC
		)
)		
WHERE rnum BETWEEN #{startRow} AND #{endRow}

SELECT no,title,content,writer,writeDate,hit
FROM board
WHERE no = #{no}

mapper.update:
UPDATE board
SET hit = hit + 1
WHERE no = #{no}

UPDATE board 
SET title = #{title}, content=#{content}, writer=#{writer}
WHERE no = #{no} AND pw = #{pw}
